  //     .then((user) => {
  //       console.log(user);
  //       setIsAuthenticated(true);
  //     })
  //     .catch((e) => {
  //       console.error(e);
  //     });
  //   }, 5000);
  // }, []);

  const [oswaldLoaded] = useOswald({
    Oswald_400Regular,
  });

